import{_ as d}from"./chunks/youtube-embed.a37e20b9.js";import{P as h}from"./chunks/ProjectCard.262e7b58.js";import{i as m,c as u,u as t,g as c,j as n,a as e,t as f,e as l,f as p,m as b,o as a}from"./chunks/framework.88e9bc5c.js";const v={key:1,controls:""},y=["src"],g=e("p",null,"Experience the magic of music like never before at Chromatone's first-ever Tutorship program free promo event!",-1),_=e("p",null,"Join us for an unforgettable evening filled with music, technology, and innovation. Meet Denis Starov, the maestro behind Chromatone, and immerse yourself in the vibrant world of visual music language.",-1),w=e("p",null,"This event is a unique blend of presentation, workshop, and performance, all set to the rhythm of Chromatone. You'll get hands-on experience with our innovative interface, exploring musical elements from basic notes to complex rhythms, from simple chords to intricate harmonies - all in a palette of colors.",-1),x=e("p",null,"Whether you're a music enthusiast or an aspiring musician, this event offers an opportunity to deepen your understanding of music and express your creativity. No prior knowledge required, just bring your enthusiasm, and if you wish, your favorite instrument.",-1),C=e("p",null,"Join us at Glowfish coworking, Bangkok, Thailand, on Saturday, September 23rd, 2023, from 5 PM to 8 PM. And who knows, if the music moves you, we might even end up in an impromptu jam session!",-1),j=e("p",null,"Don't miss this chance to be among the first to embark on this exciting journey with Chromatone. Register now at the link and step into the future of music with us.",-1),M=e("p",null,"Let's color the world with the music of Chromatone!",-1),L=e("h2",{id:"project",tabindex:"-1"},[l("Project "),e("a",{class:"header-anchor",href:"#project","aria-label":'Permalink to "Project"'},"â€‹")],-1),I=JSON.parse(`{"title":"Visual Music Lab #1","description":"Dive into the Colorful Symphony of Music with Chromatone","frontmatter":{"dynamic":true,"id":"a2480116-658a-4236-bcfd-2d79e102667c","status":"published","sort":null,"user_created":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_created":"2023-08-28T09:35:53.324Z","user_updated":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_updated":"2023-08-30T16:00:41.037Z","title":"Visual Music Lab #1","date":"2023-09-23","duration":null,"start_time":"17:00:00","description":"Dive into the Colorful Symphony of Music with Chromatone","youtube_video":null,"live_stream":null,"cover":"https://db.chromatone.center/assets/fffd364e-b0b2-46c4-8a0f-6520545c4a1a?fit=cover&format=webp&width=1000","slug":"vml-1","announcement":"","poster":"fffd364e-b0b2-46c4-8a0f-6520545c4a1a","audio":null,"url":null,"place":{"title":"Glowfish","city":"Bangkok","country":"Thailand","logo":{"id":"08b31334-5425-4986-801a-da59ca0cc31e"}},"project":{"id":"cd319553-24f9-4f29-bca2-a2c527591646","status":"published","sort":1,"user_created":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_created":"2023-08-28T09:41:07.439Z","user_updated":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_updated":"2023-08-30T15:58:11.973Z","title":"Visual Music Lab","description":"Welcome to Chromatone Music Lab, where we will embark on a journey of musical exploration, learning, playing, and composing together. Join us for a moment of sync and joy as we dive into the world of music.","cover":"ed0c4c5a-1776-42bb-9674-c093d966e84e","content":"Welcome to the Visual Music Lab, an initiative by Chromatone that invites you to experience the convergence of music and visuals in a uniquely interactive way.\\n\\nThe Visual Music Lab is a series of interactive events designed to bring the Chromatone community together. Our goal is to share the latest updates and opportunities of the Chromatone web-apps and other developments with the public. We aim to foster closer connections with our community of visual music explorers and inspire more people to follow the path of Chromatone tutorship for the benefit of all.\\n\\nThe Visual Music Lab project is an invitation to experience, learn, and contribute to the world of visual music. It's a space where music and color intermingle, where sounds are seen, and colors are heard. It's a place for exploration, discovery, and collaboration.\\n\\nAt the heart of Visual Music Lab is the Chromatone system, a unique blend of music and visual language that allows you to see music and hear colors. This system is the basis for our open-source web experiments, interactive tools, and community events.\\n\\nIn our events, we delve into the Chromatone system, exploring its possibilities and applications. From the basics of visual music language to more complex concepts, each event is an opportunity to learn, share, and grow.\\n\\nBut the Visual Music Lab is more than just a series of events. It's a community of visual music explorers, a place where artists, musicians, educators, and enthusiasts can come together to learn, share, and create.\\n\\nWhether you're a seasoned visual music explorer or just starting your journey, the Visual Music Lab offers you a platform to express your creativity, share your insights, and contribute to the future of visual music.\\n\\nJoin us in our journey to explore, create, and revolutionize the world of music. Be part of the Chromatone community and contribute to the Visual Music Lab project. Let's color the world with the music of Chromatone!","url":null,"github":null,"field":["community","education"],"start_date":"2023-08-27","end_date":null,"slug":"visual-music-lab","partners":[16],"events":["a2480116-658a-4236-bcfd-2d79e102667c"]}},"headers":[],"params":{"id":"a2480116-658a-4236-bcfd-2d79e102667c","status":"published","sort":null,"user_created":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_created":"2023-08-28T09:35:53.324Z","user_updated":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_updated":"2023-08-30T16:00:41.037Z","title":"Visual Music Lab #1","date":"2023-09-23","duration":null,"start_time":"17:00:00","description":"Dive into the Colorful Symphony of Music with Chromatone","youtube_video":null,"live_stream":null,"cover":"fffd364e-b0b2-46c4-8a0f-6520545c4a1a","slug":"vml-1","announcement":"","poster":"fffd364e-b0b2-46c4-8a0f-6520545c4a1a","audio":null,"url":null,"place":{"title":"Glowfish","city":"Bangkok","country":"Thailand","logo":{"id":"08b31334-5425-4986-801a-da59ca0cc31e"}},"project":{"id":"cd319553-24f9-4f29-bca2-a2c527591646","status":"published","sort":1,"user_created":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_created":"2023-08-28T09:41:07.439Z","user_updated":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_updated":"2023-08-30T15:58:11.973Z","title":"Visual Music Lab","description":"Welcome to Chromatone Music Lab, where we will embark on a journey of musical exploration, learning, playing, and composing together. Join us for a moment of sync and joy as we dive into the world of music.","cover":"ed0c4c5a-1776-42bb-9674-c093d966e84e","content":"Welcome to the Visual Music Lab, an initiative by Chromatone that invites you to experience the convergence of music and visuals in a uniquely interactive way.\\n\\nThe Visual Music Lab is a series of interactive events designed to bring the Chromatone community together. Our goal is to share the latest updates and opportunities of the Chromatone web-apps and other developments with the public. We aim to foster closer connections with our community of visual music explorers and inspire more people to follow the path of Chromatone tutorship for the benefit of all.\\n\\nThe Visual Music Lab project is an invitation to experience, learn, and contribute to the world of visual music. It's a space where music and color intermingle, where sounds are seen, and colors are heard. It's a place for exploration, discovery, and collaboration.\\n\\nAt the heart of Visual Music Lab is the Chromatone system, a unique blend of music and visual language that allows you to see music and hear colors. This system is the basis for our open-source web experiments, interactive tools, and community events.\\n\\nIn our events, we delve into the Chromatone system, exploring its possibilities and applications. From the basics of visual music language to more complex concepts, each event is an opportunity to learn, share, and grow.\\n\\nBut the Visual Music Lab is more than just a series of events. It's a community of visual music explorers, a place where artists, musicians, educators, and enthusiasts can come together to learn, share, and create.\\n\\nWhether you're a seasoned visual music explorer or just starting your journey, the Visual Music Lab offers you a platform to express your creativity, share your insights, and contribute to the future of visual music.\\n\\nJoin us in our journey to explore, create, and revolutionize the world of music. Be part of the Chromatone community and contribute to the Visual Music Lab project. Let's color the world with the music of Chromatone!","url":null,"github":null,"field":["community","education"],"start_date":"2023-08-27","end_date":null,"slug":"visual-music-lab","partners":[16],"events":["a2480116-658a-4236-bcfd-2d79e102667c"]}},"relativePath":"academy/events/vml-1/index.md","filePath":"academy/events/vml-1/index.md","lastUpdated":0}`),V={name:"academy/events/vml-1/index.md"},S=Object.assign(V,{setup(k){const{params:i,frontmatter:o}=m();return(T,B)=>{var r;const s=d;return a(),u("div",null,[t(o).youtube_video?(a(),c(s,{key:0,video:t(o).youtube_video},null,8,["video"])):n("",!0),e("p",null,f(t(i).announce?"> "+t(i).announce:""),1),t(o).audio?(a(),u("audio",v,[e("source",{src:`https://db.chromatone.center/assets/${t(o).audio}`,type:"audio/mpeg"},null,8,y),l(" Your browser does not support the audio element. ")])):n("",!0),g,_,w,x,C,j,M,L,p(h,b({class:"m-4 max-w-55ch"},(r=t(o))==null?void 0:r.project),null,16),t(o).live_stream?(a(),c(s,{key:2,video:t(o).live_stream},null,8,["video"])):n("",!0)])}}});export{I as __pageData,S as default};
