import{_ as t,c as e,o as a,e as s}from"./chunks/framework.233ced2b.js";const o="/assets/sun.8c4fadbd.svg",r="/assets/sun-granules.7d7542a9.jpg",n="/assets/Sunspot.0f2d9981.jpg",i="/assets/extreme_ultraviolet_sun.6b628c68.jpg",b=JSON.parse('{"title":"The Sun","description":"The Sun is the center of our Solar system and is the main source of all light on Earth.","frontmatter":{"title":"The Sun","description":"The Sun is the center of our Solar system and is the main source of all light on Earth.","date":"2021-08-30T00:00:00.000Z","cover":"sun.jpg"},"headers":[],"relativePath":"theory/color/light/sun/index.md","filePath":"theory/color/light/sun/index.md","lastUpdated":1676883357000}'),h={name:"theory/color/light/sun/index.md"},c=s('<p>The Sun is the center of our Solar system and is the main source of all light on Earth.</p><p>The Sun is a G-type main-sequence star that comprises about 99.86% of the mass of the Solar System. The Sun has an absolute magnitude of +4.83, estimated to be brighter than about 85% of the stars in the Milky Way, most of which are red dwarfs.</p><p><img src="'+o+'" alt="svg"></p><p>The Sun is by far the brightest object in the Earth&#39;s sky, with an apparent magnitude of −26.74. This is about 13 billion times brighter than the next brightest star, Sirius, which has an apparent magnitude of −1.46.</p><p>Thermonuclear reactions in its core produce high energy gamma-rays that are absorbed and converted into lower energy radiation by ionized atoms in its photosphere and chromosphere layers.</p><p><img src="'+r+'" alt=""></p><p><img src="'+n+'" alt=""></p><h2 id="uv-radiation-of-the-sun" tabindex="-1">UV radiation of the Sun <a class="header-anchor" href="#uv-radiation-of-the-sun" aria-label="Permalink to &quot;UV radiation of the Sun&quot;">​</a></h2><p><img src="'+i+'" alt=""></p>',9),d=[c];function l(p,u,m,_,f,g){return a(),e("div",null,d)}const y=t(h,[["render",l]]);export{b as __pageData,y as default};
