import{_ as i}from"./chunks/YoutubeEmbed.5699508b.js";import{_ as n,c as s,g as o,a as e,I as a,b as r,o as l}from"./chunks/framework.916f0e04.js";const k=JSON.parse('{"title":"Electronic minimalism","description":"Ways to produce compelling electronic compositions","frontmatter":{"title":"Electronic minimalism","description":"Ways to produce compelling electronic compositions","date":"2021-07-03T00:00:00.000Z","cover":"underdog.jpg"},"headers":[],"relativePath":"theory/composition/electronic/index.md","filePath":"theory/composition/electronic/index.md","lastUpdated":1689081471000}'),h={name:"theory/composition/electronic/index.md"},c=e("h2",{id:"the-4-principles-of-techno-minimalism-by-underdog",tabindex:"-1"},[a("The 4 principles of techno minimalism by "),e("a",{href:"https://underdog.brussels/",target:"_blank",rel:"noreferrer"},"Underdog"),a(),e("a",{class:"header-anchor",href:"#the-4-principles-of-techno-minimalism-by-underdog","aria-label":'Permalink to "The 4 principles of techno minimalism by [Underdog](https://underdog.brussels/)"'},"​")],-1),d=r('<h2 id="_1-subtle-motifs" tabindex="-1">1. Subtle motifs <a class="header-anchor" href="#_1-subtle-motifs" aria-label="Permalink to &quot;1. Subtle motifs&quot;">​</a></h2><h3 id="two-elements-bouncing-at-each-other" tabindex="-1">Two elements bouncing at each other <a class="header-anchor" href="#two-elements-bouncing-at-each-other" aria-label="Permalink to &quot;Two elements bouncing at each other&quot;">​</a></h3><p>Ensure something is going on in the lows, in the mids &amp; the highs. Choose a phrase length &amp; design it to create a simple groove.</p><h2 id="_2-movement" tabindex="-1">2. Movement <a class="header-anchor" href="#_2-movement" aria-label="Permalink to &quot;2. Movement&quot;">​</a></h2><h3 id="too-little-is-boring-too-much-is-distracting" tabindex="-1">Too little is boring, too much is distracting <a class="header-anchor" href="#too-little-is-boring-too-much-is-distracting" aria-label="Permalink to &quot;Too little is boring, too much is distracting&quot;">​</a></h3><p>Create movement in the timbre of your elements. Use modulating effects, tweak filters, change envelope lengths, LFOs or automation to create movement.</p><h2 id="_3-polymeters" tabindex="-1">3. Polymeters <a class="header-anchor" href="#_3-polymeters" aria-label="Permalink to &quot;3. Polymeters&quot;">​</a></h2><h3 id="slightly-unstable-but-exciting-in-moderation" tabindex="-1">Slightly unstable, but exciting in moderation <a class="header-anchor" href="#slightly-unstable-but-exciting-in-moderation" aria-label="Permalink to &quot;Slightly unstable, but exciting in moderation&quot;">​</a></h3><p>Simple grooves become endlesly listenable when you add 1 or 2 polymeters to them. During breaks polymeters make it hard to guess where the ‘1’ of the groove is, which is satisfying, when resolved.</p><h2 id="_4-tension-and-release" tabindex="-1">4. Tension and release <a class="header-anchor" href="#_4-tension-and-release" aria-label="Permalink to &quot;4. Tension and release&quot;">​</a></h2><h3 id="make-the-audience-wait" tabindex="-1">Make the audience wait <a class="header-anchor" href="#make-the-audience-wait" aria-label="Permalink to &quot;Make the audience wait&quot;">​</a></h3><p>Evolve the global story of the track to build up tension and then release that tension. Make them want it, tease them, drive them crazy! Use contrast (busy vs sparse). Use reversed reverb tails as buildups.</p>',12);function m(u,p,b,g,_,f){const t=i;return l(),s("div",null,[c,o(t,{video:"mh8jV6IGI84"}),d,o(t,{video:"B_D3dCSylCg"})])}const x=n(h,[["render",m]]);export{k as __pageData,x as default};
