import{aD as n}from"./chunks/theme.Z4NKC_0E.js";import{_ as s,g as r,V as t,h as i,ae as a,W as o,f as l}from"./chunks/framework.B7TlvOs3.js";const q=JSON.parse('{"title":"Electronic minimalism","description":"Ways to produce compelling electronic compositions","frontmatter":{"title":"Electronic minimalism","description":"Ways to produce compelling electronic compositions","date":"2021-07-03T00:00:00.000Z","cover":"underdog.jpg"},"headers":[],"relativePath":"theory/composition/electronic/index.md","filePath":"theory/composition/electronic/index.md","lastUpdated":1725362049000}'),h={name:"theory/composition/electronic/index.md"},m=i("h2",{id:"the-4-principles-of-techno-minimalism-by-underdog",tabindex:"-1"},[a("The 4 principles of techno minimalism by "),i("a",{href:"https://underdog.brussels/",target:"_blank",rel:"noreferrer"},"Underdog"),a(),i("a",{class:"header-anchor",href:"#the-4-principles-of-techno-minimalism-by-underdog","aria-label":'Permalink to "The 4 principles of techno minimalism by [Underdog](https://underdog.brussels/)"'},"​")],-1),c=o('<h2 id="_1-subtle-motifs" tabindex="-1">1. Subtle motifs <a class="header-anchor" href="#_1-subtle-motifs" aria-label="Permalink to &quot;1. Subtle motifs&quot;">​</a></h2><h3 id="two-elements-bouncing-at-each-other" tabindex="-1">Two elements bouncing at each other <a class="header-anchor" href="#two-elements-bouncing-at-each-other" aria-label="Permalink to &quot;Two elements bouncing at each other&quot;">​</a></h3><p>Ensure something is going on in the lows, in the mids &amp; the highs. Choose a phrase length &amp; design it to create a simple groove.</p><h2 id="_2-movement" tabindex="-1">2. Movement <a class="header-anchor" href="#_2-movement" aria-label="Permalink to &quot;2. Movement&quot;">​</a></h2><h3 id="too-little-is-boring-too-much-is-distracting" tabindex="-1">Too little is boring, too much is distracting <a class="header-anchor" href="#too-little-is-boring-too-much-is-distracting" aria-label="Permalink to &quot;Too little is boring, too much is distracting&quot;">​</a></h3><p>Create movement in the timbre of your elements. Use modulating effects, tweak filters, change envelope lengths, LFOs or automation to create movement.</p><h2 id="_3-polymeters" tabindex="-1">3. Polymeters <a class="header-anchor" href="#_3-polymeters" aria-label="Permalink to &quot;3. Polymeters&quot;">​</a></h2><h3 id="slightly-unstable-but-exciting-in-moderation" tabindex="-1">Slightly unstable, but exciting in moderation <a class="header-anchor" href="#slightly-unstable-but-exciting-in-moderation" aria-label="Permalink to &quot;Slightly unstable, but exciting in moderation&quot;">​</a></h3><p>Simple grooves become endlesly listenable when you add 1 or 2 polymeters to them. During breaks polymeters make it hard to guess where the ‘1’ of the groove is, which is satisfying, when resolved.</p><h2 id="_4-tension-and-release" tabindex="-1">4. Tension and release <a class="header-anchor" href="#_4-tension-and-release" aria-label="Permalink to &quot;4. Tension and release&quot;">​</a></h2><h3 id="make-the-audience-wait" tabindex="-1">Make the audience wait <a class="header-anchor" href="#make-the-audience-wait" aria-label="Permalink to &quot;Make the audience wait&quot;">​</a></h3><p>Evolve the global story of the track to build up tension and then release that tension. Make them want it, tease them, drive them crazy! Use contrast (busy vs sparse). Use reversed reverb tails as buildups.</p>',12),d=o('<h2 id="making-minimalist-music" tabindex="-1">Making minimalist music <a class="header-anchor" href="#making-minimalist-music" aria-label="Permalink to &quot;Making minimalist music&quot;">​</a></h2><blockquote><p>Only Having What You Need</p></blockquote><p>Write as little as possible.</p><h3 id="rule-of-three" tabindex="-1">Rule of three <a class="header-anchor" href="#rule-of-three" aria-label="Permalink to &quot;Rule of three&quot;">​</a></h3><p>At maximum three layers of music can have listeners attention.</p><h3 id="avoid-habits-and-answer-questions" tabindex="-1">Avoid habits and answer questions <a class="header-anchor" href="#avoid-habits-and-answer-questions" aria-label="Permalink to &quot;Avoid habits and answer questions&quot;">​</a></h3><ul><li>Why am I doing this?</li><li>What is doing this adding to the final result?</li><li>Is this really making any difference?</li><li>Is it really necessary?</li></ul><p>Minimalism and why it might be the most effective way to finish more music.</p>',8);function u(b,p,g,f,y,_){const e=n;return l(),r("div",null,[m,t(e,{video:"mh8jV6IGI84"}),c,t(e,{video:"B_D3dCSylCg"}),d,t(e,{video:"8FNmtguAJGE"})])}const x=s(h,[["render",u]]);export{q as __pageData,x as default};
