import{aC as r}from"./chunks/theme.BCAJ9CZC.js";import{_ as s,h,ao as o,t as n,i as a,aI as i,c as l}from"./chunks/framework.DqGUeSFU.js";const u="/assets/Pythagoras_and_Philolaus.BBZ8Yrcm.png",d="/assets/Banchieri1.BBVViWVC.jpg",k=JSON.parse('{"title":"Harmony study","description":"Different approaches to harmony in music and everything else","frontmatter":{"title":"Harmony study","description":"Different approaches to harmony in music and everything else","date":"2021-11-10T00:00:00.000Z"},"headers":[],"relativePath":"theory/harmony/study/index.md","filePath":"theory/harmony/study/index.md","lastUpdated":1738735655000}'),c={name:"theory/harmony/study/index.md"};function m(p,e,f,g,y,b){const t=r;return l(),h("div",null,[e[4]||(e[4]=o("",21)),n(t,{video:"eRkgK4jfi6M"}),e[5]||(e[5]=a("h2",{id:"balkan-vocal-harmony",tabindex:"-1"},[i("Balkan Vocal Harmony "),a("a",{class:"header-anchor",href:"#balkan-vocal-harmony","aria-label":'Permalink to "Balkan Vocal Harmony"'},"​")],-1)),n(t,{video:"AFgzzWT3zX4"}),e[6]||(e[6]=a("h2",{id:"greece",tabindex:"-1"},[i("Greece "),a("a",{class:"header-anchor",href:"#greece","aria-label":'Permalink to "Greece"'},"​")],-1)),n(t,{video:"dm1MWr0ZNdI"}),e[7]||(e[7]=a("h2",{id:"georgia",tabindex:"-1"},[i("Georgia "),a("a",{class:"header-anchor",href:"#georgia","aria-label":'Permalink to "Georgia"'},"​")],-1)),n(t,{video:"9HtdXXOTWlQ"}),e[8]||(e[8]=i()),e[9]||(e[9]=a("p",null,"Georgian polyphonic singing encompasses several distinct regional styles, each with its own unique characteristics. The most prominent styles include Eastern Georgian and Western Georgian polyphony. Eastern Georgian polyphony, found in regions like Kartli and Kakheti, typically employs pedal drone polyphony. This style features two highly embellished melodic lines developing rhythmically free against a background of sustained pitches. In contrast, Western Georgian polyphony, prevalent in areas such as Achara, Imereti, Samegrelo, and Guria, utilizes contrapuntal techniques, often resulting in three and four-part harmonies with highly individualized melodic lines in each part.",-1)),e[10]||(e[10]=a("p",null,'A key characteristic of Georgian polyphonic singing is its extensive use of sharp dissonant harmonies, including seconds, fourths, sevenths, and ninths. The so-called "Georgian Triad" (C-F-G), consisting of a fourth and a second above the bass note, is particularly common. Additionally, Georgian music is known for colorful modulations and unusual key changes. The vocal technique often involves male falsetto singers, known as "krimanchuli," performing high-pitched melodies above the main melody. This creates a unique and ethereal quality to the music.',-1)),e[11]||(e[11]=a("p",null,"Georgian polyphonic singing typically involves three main vocal parts: mtkmeli (second tenor), modzakhili (first tenor), and bani (baritone). Other specialized parts include krini (falsetto), dvrini (bass), gamqivani, shemkhmobari, and krimanchuli. While most songs are sung a cappella, some may feature instrumental accompaniment on traditional instruments like the chonguri or panduri. The polyphonic structure allows for partial improvisation, with singers often creating their own melodies and harmonies within the established framework, resulting in a dynamic and engaging musical dialogue between voices.",-1)),e[12]||(e[12]=a("h2",{id:"pygmy-music",tabindex:"-1"},[i("Pygmy music "),a("a",{class:"header-anchor",href:"#pygmy-music","aria-label":'Permalink to "Pygmy music"'},"​")],-1)),n(t,{video:"fHNkSYXbVMI"}),e[13]||(e[13]=i()),e[14]||(e[14]=a("p",null,'Pygmy music refers to the sub-Saharan African music traditions of the Central African foragers (or "Pygmies"), predominantly in the Congo, the Central African Republic and Cameroon.',-1)),e[15]||(e[15]=a("p",null,"Pygmy groups include the Bayaka, the Mbuti, and the Batwa.",-1)),e[16]||(e[16]=a("p",null,"Music is an important part of Pygmy life, and casual performances take place during many of the day's events. Music comes in many forms, including the spiritual likanos stories, vocable singing and music played from a variety of instruments including the bow harp (ieta), ngombi (harp zither) and limbindi (a string bow).",-1)),e[17]||(e[17]=a("p",null,"Researchers who have studied Pygmy music include Simha Arom, Louis Sarno, Colin Turnbull and Jean-Pierre Hallet.",-1)),e[18]||(e[18]=a("h3",{id:"polyphonic-song",tabindex:"-1"},[i("Polyphonic song "),a("a",{class:"header-anchor",href:"#polyphonic-song","aria-label":'Permalink to "Polyphonic song"'},"​")],-1)),e[19]||(e[19]=a("p",null,"The Mbenga (Aka/Benzele) and Baka peoples in the west and the Mbuti (Efé) in the east are particularly known for their dense contrapuntal communal improvisation. Simha Arom says that the level of polyphonic complexity of Mbenga–Mbuti music was reached in Europe only in the 14th century. The polyphonic singing of the Aka Pygmies was relisted on the Representative List of the Intangible Cultural Heritage of Humanity in 2008.",-1)),e[20]||(e[20]=a("p",null,'Mbenga–Mbuti Pygmy music consists of up to four parts and can be described as an "ostinato with variations" similar to a passacaglia in that it is cyclical. It is based on repetition of periods of equal length that each singer divides using different rhythmic figures specific to different repertoires and songs. This creates a detailed surface and endless variations not only of the same period repeated but of various performances of the same piece of music. As in some Balinese gamelan music, these patterns are based on a super-pattern which is never heard. The Pygmies themselves do not learn or think of their music in this theoretical framework, but learn the music growing up.',-1)),e[21]||(e[21]=a("p",null,"Polyphonic music is only characteristic of the Mbenga and Mbuti. The Gyele/Kola, Great Lakes Twa and Southern Twa have very different musical styles.",-1)),a("blockquote",null,[n(t,{video:"zb0z0yOdY5E"}),e[0]||(e[0]=i()),e[1]||(e[1]=a("p",null,"Most of the literature on Baka music concentrates on their “spirit dances”. What is actually meant by “spirit” is rarely, if ever, mentioned in the literature. In English the language for such things as “spirit” and “enchantment” have been heavily influenced by 1000 years of Christianity and given negative connotations, so the true meanings of words get lost in translation.There seems to be an underlying inference that belief in “spirits” is a primitive animistic practice, rather than a rational interpretation of a real phenomenon that is indirectly revered in Western society in art, performance and sport, but which is never spoken about. The Baka call this mé. With their combination of polyphonic singing, polyrhythmic percussion and masked dancers, the Baka are experts at manifesting mé. Each “Spirit Dance” creates its own unique emotions personified in the mé. By being present at these “spirit dances” from before they are born, aware of sounds and movements while still in their mother’s womb, Baka children grow up learning that the purpose of “musicking” is not about performing songs, but about manifesting mé for the good of all.",-1))]),e[22]||(e[22]=a("h3",{id:"liquindi",tabindex:"-1"},[i("Liquindi "),a("a",{class:"header-anchor",href:"#liquindi","aria-label":'Permalink to "Liquindi"'},"​")],-1)),n(t,{video:"ZNzX5t5S4Ls"}),e[23]||(e[23]=i()),e[24]||(e[24]=a("p",null,"Liquindi is water drumming, typically practiced by Pygmy women and girls. The sound is produced by persons standing in water, and hitting the surface of the water with their hands, such as to trap air in the hands and produce a percussive effect that arises by sudden change in air pressure of the trapped air. The sound cannot exist entirely in water, since it requires the air-water boundary as a surface to be struck, so the sound is not hydraulophonic.",-1)),n(t,{video:"8QyK0q1Kr-A"}),e[25]||(e[25]=o("",11)),n(t,{video:"FclwRECHoWc"}),e[26]||(e[26]=i()),e[27]||(e[27]=a("h3",{id:"khoikhoi-and-san-music-southern-africa",tabindex:"-1"},[i("Khoikhoi and San Music (Southern Africa) "),a("a",{class:"header-anchor",href:"#khoikhoi-and-san-music-southern-africa","aria-label":'Permalink to "Khoikhoi and San Music (Southern Africa)"'},"​")],-1)),e[28]||(e[28]=a("p",null,"Features overlapping vocal parts and complex rhythms.",-1)),n(t,{video:"dTL_TdONVBs"}),e[29]||(e[29]=i()),e[30]||(e[30]=a("h3",{id:"west-african-griot",tabindex:"-1"},[i("West African Griot "),a("a",{class:"header-anchor",href:"#west-african-griot","aria-label":'Permalink to "West African Griot"'},"​")],-1)),e[31]||(e[31]=a("p",null,"While primarily melodic, some griot performances include harmonized choruses.",-1)),a("blockquote",null,[n(t,{video:"Ig91Z0-rBfo"}),e[2]||(e[2]=i()),e[3]||(e[3]=a("p",null,"Sona Jobarteh is the first female Kora virtuoso to come from a west African Griot family. The Kora is one of the most important instruments belonging to the Manding peoples of West Africa (Gambia, Senegal, Mali, Guinea and Guinea-Bissau). It belongs exclusively to griot families, and usually only men who are born into these families have the right to take up the instrument professionally. Sona Jobarteh combines various genres of African Music and western musical elements.",-1))]),n(t,{video:"--q1j2PExpE"})])}const q=s(c,[["render",m]]);export{k as __pageData,q as default};
