import{aC as s}from"./chunks/theme.DkSlrVce.js";import{_ as l}from"./chunks/BeatBars.CximAi49.js";import{d as h}from"./chunks/rhythms.Braq9F-U.js";import{h as c,t,x as u,y as d,u as m,i as a,aI as p,ao as n,c as b}from"./chunks/framework.DqGUeSFU.js";import"./chunks/trash.CxB0FQaN.js";const T=JSON.parse('{"title":"Afro-Cuban clave","description":"African, Cuban and Latin American rhythm patterns","frontmatter":{"title":"Afro-Cuban clave","description":"African, Cuban and Latin American rhythm patterns","date":"2021-10-21T00:00:00.000Z","cover":"yuting-gao.jpg","urls":["https://en.wikipedia.org/wiki/Tumbao","https://en.wikipedia.org/wiki/Clave_(rhythm)","https://en.wikipedia.org/wiki/Tresillo_(rhythm)"]},"headers":[],"relativePath":"theory/rhythm/system/clave/index.md","filePath":"theory/rhythm/system/clave/index.md","lastUpdated":1738737326000}'),f={name:"theory/rhythm/system/clave/index.md"},A=Object.assign(f,{setup(y){const{afrocuban:r}=h;return(v,e)=>{const o=l,i=s;return b(),c("div",null,[t(o,u(d(m(r))),null,16),e[0]||(e[0]=a("h2",{id:"clave",tabindex:"-1"},[p("Clave "),a("a",{class:"header-anchor",href:"#clave","aria-label":'Permalink to "Clave"'},"​")],-1)),e[1]||(e[1]=a("p",null,"The clave (/ˈklɑːveɪ, kleɪv/; Spanish: [ˈklaβe]) is a rhythmic pattern used as a tool for temporal organization in Afro-Cuban music. In Spanish, clave literally means key, clef, code, or keystone. It is present in a variety of genres such as Abakuá music, rumba, conga, son, mambo, salsa, songo, timba and Afro-Cuban jazz. The five-stroke clave pattern represents the structural core of many Afro-Cuban rhythms.",-1)),e[2]||(e[2]=a("p",null,"The clave pattern originated in sub-Saharan African music traditions, where it serves essentially the same function as it does in Cuba. In ethnomusicology, clave is also known as a key pattern, guide pattern, phrasing referent, timeline, or asymmetrical timeline. The clave pattern is also found in the African diaspora music of Haitian Vodou drumming, Afro-Brazilian music, African-American music, Louisiana Voodoo drumming, and Afro-Uruguayan music (candombe). The clave pattern (or hambone, as it is known in the United States) is used in North American popular music as a rhythmic motif or simply a form of rhythmic decoration.",-1)),e[3]||(e[3]=a("p",null,"The historical roots of the clave are linked to transnational musical exchanges within the African diaspora. For instance, influences of the African “bomba” rhythm are reflected in the clave. In addition to this, the emphasis and role of the drum within the rhythmic patterns speaks further to these diasporic roots.",-1)),e[4]||(e[4]=a("p",null,"The clave is the foundation of reggae, reggaeton, and dancehall. In this sense, it is the “heartbeat” that underlies the essence of these genres. The rhythms and vibrations are universalized in that they demonstrate a shared cultural experience and knowledge of these roots. Ultimately, this embodies the diasporic transnational exchange.",-1)),e[5]||(e[5]=a("p",null,"In considering the clave as this basis of cultural understanding, relation, and exchange, this speaks to the transnational influence and interconnectedness of various communities. This musical fusion is essentially what constitutes the flow and foundational “heartbeat” of a variety of genres.",-1)),t(i,{video:"d1tiv0Ep0kA"}),e[6]||(e[6]=n("",8)),t(i,{video:"LlDgRYKyQAE"}),e[7]||(e[7]=n("",26)),t(i,{video:"1QFU5tJc9ok"}),e[8]||(e[8]=n("",65)),t(i,{video:"DhOLGoBCDjw"}),e[9]||(e[9]=n("",11))])}}});export{T as __pageData,A as default};
